import React from "react";
import PopupWithForm from "./PopupWithForm";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function EditAvatarPopup(props){

  return(
    <PopupWithForm type="avatar-confirm" name="delete-confirm" title="Обновить аватар" isOpen={props.isOpen} onClose={props.onClose}>
        <input id="avatar" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
        <span id="error-avatar" className="error-message error-message_visible"></span>
    </PopupWithForm>
  )
}

export default EditAvatarPopup;
