function Card(props){

    function handleClick() {
        props.onCardClick(props.card);
      } 

    return(
        <article className="card" onClick={handleClick}>
            <button aria-label="Удалить" type="button" className="card__delete-btn">
            </button>
            <img src={props.data.link} alt="фоновая картинка карточки" className="card__img"/>
            <div className="card__content">
                <h2 className="name name_place_card">{props.data.name}</h2>
                <div className="card__likes-wrapper">
                <button aria-label="Нравится" type="button" className="card__like-btn"></button>
                <span className="card__like-count">{props.data.likes.length}</span>
                </div>
            </div>
        </article>
    )
}

export default Card;
