import React, { useState } from 'react';
import Header from './Header';

const Login = (props) => {
  const [formParams, setFormParams] = useState({
    email: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormParams((prev) => ({
      ...prev,
      [name]: value
    }));
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    let { email, password} = formParams;
    props.handleLogin({ email, password })
    .catch(err => {
      setMessage(err.message);
    });
  }

  return(
    <div className="page">
      <Header loggedIn={props.loggedIn}/>
      <div className="register__wrapper">
        <div className="register">
          <h1 className="name name_place-register">
            Вход
          </h1>
          <form onSubmit={handleSubmit} className="register__form">
            <input id="email" 
                  name="email" 
                  type="email" 
                  value={formParams.email} 
                  onChange={handleChange} 
                  className="popup__input popup__input_color-invert"
                  placeholder="Email"  
                  required
                  />
            <input id="password"  
                    name="password" 
                    type="password" 
                    value={formParams.password} 
                    onChange={handleChange} 
                    className="popup__input popup__input_color-invert"
                    placeholder="Пароль"  
                    required/>
            <div className="register__button-container">
              <button type="submit" className="popup__submit popup__submit_color-white">Войти</button>
              <p className="register__error">{message}</p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
