import React, {useEffect} from "react";

import editButton from '../images/profile/edit-btn-icon.svg';
import {api} from "../utils/Api"
import Card from "./Card";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {CardsContext} from "../contexts/CardsContext";

function Main({onEditProfile, onAddPlace, onEditAvatar, onCardClick}) {
  const [cards, setCards] = React.useState([]);
  const currentUser = React.useContext(CurrentUserContext);

  useEffect(() => {
    api.getInitialCards()
      .then((cardsInfo) => {
        setCards(cardsInfo);
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
    },[])

  function handleLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  function handleCardDelete(card){
    api.deleteCard(card._id).then(() => {
      setCards((state) => state.filter((c) => c._id !== card._id));
    });
  }  

  return (
      <main>
        <section className="profile">
          <div className="profile__content">
            <div className="profile__avatar-container">
              <img src={currentUser.avatar} alt="Аватар" className="profile__avatar"/>
            <div className="profile__avatar-overlay">
              <img onClick={onEditAvatar} src={editButton} alt="Редактировать" className="profile__avatar-edit"/>
            </div>
          </div>
          <div className="profile__info">
            <div className="profile__info-main">
              <h1 className="profile__info-name">{currentUser.name}</h1>
              <button onClick={onEditProfile} aria-label="Редактироавние профиля" type="button" className="profile__info-edit">
              </button>
            </div>
            <p className="profile__info-job">{currentUser.about}</p>
          </div>
        </div>
        <button onClick={onAddPlace} aria-label="Добавить" type="button" className="profile__add-btn">
        </button>
      </section>
      <section className="elements">
          {cards.map((card) => (
            <CardsContext.Provider value={card}>
              <Card onCardClick={onCardClick} 
                    onCardLike={handleLike} 
                    onCardDelete={handleCardDelete} 
                    key={card._id}/>
            </CardsContext.Provider>
          ))}
      </section>
      </main>
  );
}

export default Main;
