import React, {useEffect} from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import {api} from "../utils/Api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null); 
  const [currentUser, setCurrentUser] = React.useState({});

  useEffect(() => {
    Promise(api.getProfile())
      .then((userInfo) => {
        setCurrentUser(userInfo);
        console.log(currentUser);
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
    },[])

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  }

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  }

  const handleCardClick = (card) => {
    setSelectedCard({ 
      name: card.name, 
      link: card.link, 
    });
  }

  const handleClosePopup = () => {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null)
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <Header />
          <Main onEditProfile={handleEditProfileClick} 
                onAddPlace={handleAddPlaceClick} 
                onEditAvatar={handleEditAvatarClick} 
                onCardClick={handleCardClick}/>
          <Footer />
        </div>
        <PopupWithForm type="bio" name="profile-edit" title="Редактировать профиль" isOpen={isEditProfilePopupOpen} onClose={handleClosePopup}>
            <input id="firstname" type="text" name="firstname" className="popup__input popup__input_content_user-name" placeholder="Введите имя" required minLength="2" maxLength="40"/>
            <span id="error-firstname" className="error-message error-message_visible"></span>
            <input id="job" type="text" name="job" className="popup__input popup__input_content_job" placeholder="Введите профессию" required minLength="2" maxLength="200"/>
            <span id="error-job" className="error-message error-message_visible"></span>
        </PopupWithForm>
        <PopupWithForm type="delete-confirm" name="avatar-edit-confirm" title="Вы уверены?">
        </PopupWithForm>
        <PopupWithForm type="avatar-confirm" name="delete-confirm" title="Обновить аватар" isOpen={isEditAvatarPopupOpen} onClose={handleClosePopup}>
            <input id="avatar" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
            <span id="error-avatar" className="error-message error-message_visible"></span>
        </PopupWithForm>
        <PopupWithForm type="place" name="place-add" title="Новое место" isOpen={isAddPlacePopupOpen} onClose={handleClosePopup}>
            <input id="place-name" type="text" name="place-name" className="popup__input popup__input_content_place-name" placeholder="Название" required minLength="2" maxLength="30"/>
            <span id="error-place-name" className="error-message error-message_visible"></span>
            <input id="image" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
            <span id="error-image" className="error-message error-message_visible"></span>
        </PopupWithForm>
        {selectedCard && <ImagePopup card={selectedCard} onClose={handleClosePopup}/>}
      </div>
    </CurrentUserContext.Provider>

  );
}

export default App;
