import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = (props) => {
  const [formParams, setFormParams] = useState({
    username: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormParams((prev) => ({
      ...prev,
      [name]: value
    }));
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formParams.username || !formParams.password){
      return;
    }
    props.handleLogin({ username: formParams.username, password: formParams.password })
        .catch(err => {
          setMessage(err.message);
        });
  }

  return(
    <div className="register">
    <h1 className="register__welcome">
      Вход
    </h1>
    <p className="register__error">
      {message}
    </p>
    <form onSubmit={handleSubmit} className="register__form">
      <label htmlFor="username">
        Логин:
      </label>
      <input id="username" name="username" type="text" value={formParams.username} onChange={handleChange} />
      <label htmlFor="email">
        Email:
      </label>
      <input id="email" name="email" type="email" value={formParams.email} onChange={handleChange} />
      <div className="register__button-container">
        <button type="submit" className="register__link">Войти</button>
      </div>
    </form>
  </div>
  );
}

export default Login;
