import React from "react";
import {CardsContext} from "../contexts/CardsContext";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function Card(props){
  const cardData = React.useContext(CardsContext);
  const currentUser = React.useContext(CurrentUserContext);

  const isOwn = cardData.owner._id === currentUser._id;
  const cardDeleteButtonClassName = (
    `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
    );    

  const isLiked = cardData.likes.some(i => i._id === currentUser._id);
  const cardLikeButtonClassName = (
    `card__like-btn ${isLiked ? 'card__like-btn_state_active' : ''}`
    );

  function handleClick() {
    props.onCardClick(cardData);
  }  

  function handleLikeClick(){
    props.onCardLike(cardData);
  }

  return(
    <article className="card">
      <button aria-label="Удалить" type="button" className={cardDeleteButtonClassName}>
      </button>
      <img src={cardData.link} alt={cardData.name} className="card__img" onClick={handleClick}/>
      <div className="card__content">
        <h2 className="name name_place_card">{cardData.name}</h2>
        <div className="card__likes-wrapper">
        <button aria-label="Нравится" type="button" className={cardLikeButtonClassName} onClick={handleLikeClick}></button>
        <span className="card__like-count">{cardData.likes.length}</span>
        </div>
      </div>
    </article>
  )
}

export default Card;
