function PopupWithForm(props){
    // props: type - class in popup container, name - name&id in form, title - h2 content
    return(
        <div className={`popup popup_type_${props.type} ${props.isOpen ? 'popup_opened' : ''}`}>
        <div className="popup__container popup__container_type_form">
          <button aria-label="Закрыть" type="button" className="popup__close-btn" onClick={props.onClose}>
          </button>
          <h2 className="name name_place_popup">{props.title}</h2>
          <form name={props.name} id={`form-${props.name}`} className="popup__form" noValidate>
            {props.children}
            <button aria-label="Отправить" type="submit" className="popup__submit">Сохранить</button>
          </form>
        </div>
      </div>
    )
}

export default PopupWithForm;
