import React from "react";
import ava from '../images/profile/ava.jpg';
import editButton from '../images/profile/edit-btn-icon.svg';
import {api} from "../utils/Api"



function Main(props) {
  const [userName, setUserName] = React.useState('User Name');

  Promise.all([api.getProfile()])
  .then((res) => {
    console.log(res);
    setUserName(res.name);
    // // тут установка данных пользователя
    // userInfo.setUserInfo(userData);
    // userId = userData._id;
    // // // отрисовка карточек
    // section.renderItems(cards);
  })
  .catch(err => {
    console.log('Ошибка: ', err)
  });

  return (
      <main>
        <section className="profile">
          <div className="profile__content">
            <div className="profile__avatar-container">
              <img src={ava} alt="Аватар" className="profile__avatar"/>
            <div className="profile__avatar-overlay">
              <img onClick={props.onEditAvatar} src={editButton} alt="Редактировать" className="profile__avatar-edit"/>
            </div>
          </div>
          <div className="profile__info">
            <div className="profile__info-main">
              <h1 className="profile__info-name"> Жак</h1>
              <button onClick={props.onEditProfile} aria-label="Редактироавние профиля" type="button" className="profile__info-edit">
              </button>
            </div>
            <p className="profile__info-job">Пьяница</p>
          </div>
        </div>
        <button onClick={props.onAddPlace} aria-label="Добавить" type="button" className="profile__add-btn">
        </button>
      </section>
      <section className="elements">
      </section>
      </main>
  );
}

export default Main;
