import React, {useState} from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true)
    // const EditProfilePopup = document.querySelector('.popup_type_bio');
    // EditProfilePopup.classList.add('popup_opened');
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true)
    // const avatarPopup = document.querySelector('.popup_type_avatar-confirm');
    // avatarPopup.classList.add('popup_opened');
  }

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true)
    // const AddPlacePopup = document.querySelector('.popup_type_place');
    // AddPlacePopup.classList.add('popup_opened');
  }

  return (
    <div className="App">
      <div className="page">
        <Header />
        <Main onEditProfile={handleEditAvatarClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditProfileClick}/>
        <Footer />
      </div>
      <PopupWithForm type="bio" name="profile-edit" title="Редактировать профиль" isOpen={isEditProfilePopupOpen}>
        <fieldset className="popup__fieldset">
          <input id="firstname" type="text" name="firstname" className="popup__input popup__input_content_user-name" placeholder="Введите имя" required minLength="2" maxLength="40"/>
          <span id="error-firstname" className="error-message error-message_visible"></span>
          <input id="job" type="text" name="job" className="popup__input popup__input_content_job" placeholder="Введите профессию" required minLength="2" maxLength="200"/>
          <span id="error-job" className="error-message error-message_visible"></span>
        </fieldset>
      </PopupWithForm>
      <PopupWithForm type="delete-confirm" name="avatar-edit-confirm" title="Вы уверены?">
      </PopupWithForm>
      <PopupWithForm type="avatar-confirm" name="delete-confirm" title="Обновить аватар" isOpen={isEditAvatarPopupOpen}>
        <fieldset className="popup__fieldset">
          <input id="avatar" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
          <span id="error-avatar" className="error-message error-message_visible"></span>
        </fieldset>
      </PopupWithForm>
      {/* <div className="popup popup_type_avatar-confirm">
        <div className="popup__container popup__container_type_form">
          <button aria-label="Закрыть" type="button" className="popup__close-btn">
          </button>
          <h2 className="name name_place_popup">Обновить аватар</h2>
          <form name="avatar-edit-confirm" id="avatar-edit-confirm" className="popup__form" noValidate> 
            <button aria-label="Отправить" type="submit" className="popup__submit">Сохранить</button>
          </form>
        </div>
      </div> */}
      <PopupWithForm type="place" name="place-add" title="Новое место" isOpen={isAddPlacePopupOpen}>
        <fieldset className="popup__fieldset">
          <input id="place-name" type="text" name="place-name" className="popup__input popup__input_content_place-name" placeholder="Название" required minLength="2" maxLength="30"/>
          <span id="error-place-name" className="error-message error-message_visible"></span>
          <input id="image" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
          <span id="error-image" className="error-message error-message_visible"></span>
        </fieldset>
      </PopupWithForm>
      <ImagePopup />
      <template id="card__template">
        <article className="card">
          <button aria-label="Удалить" type="button" className="card__delete-btn">
          </button>
          <img src="https://picsum.photos/600/600?random=1" alt="фоновая картинка карточки" className="card__img"/>
          <div className="card__content">
            <h2 className="name name_place_card">Карачаевск</h2>
            <div className="card__likes-wrapper">
              <button aria-label="Нравится" type="button" className="card__like-btn"></button>
              <span className="card__like-count"></span>
            </div>
          </div>
            </article>
      </template>
    </div>
  );
}

export default App;
