import React, {useEffect} from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import {api} from "../utils/Api";
import ava from '../images/profile/ava.jpg';
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null); 
  const [currentUser, setCurrentUser] = React.useState({name:'Жак', about:'Исследователь',avatar:ava,});
  const [cards, setCards] = React.useState([]);

  useEffect(() => {
    api.getProfile()
      .then((userInfo) => {
        setCurrentUser(userInfo);
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
    },[])

  useEffect(() => {
    api.getInitialCards()
      .then((cardsInfo) => {
        setCards(cardsInfo);
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
    },[])  

  function handleLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  function handleCardDelete(card){
    api.deleteCard(card._id).then(() => {
      setCards((state) => state.filter((c) => c._id !== card._id));
    });
  }  

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  }

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  }

  const handleCardClick = (card) => {
    setSelectedCard({ 
      name: card.name, 
      link: card.link, 
    });
  }

  const handleClosePopup = () => {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null)
  }

  const handleUpdateUser = (name, about) => {
    api.editProfile(name, about)
      .then(res => {
        setCurrentUser(res);
      })
      .then(() => {
        handleClosePopup();
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
  }

  const handleUpdateAvatar = (avatar) => {
    api.updateAvatar(avatar)
      .then(res => {
        setCurrentUser(res);
      })
      .then(() => {
        handleClosePopup();
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <Header />
          <Main onEditProfile={handleEditProfileClick} 
                onAddPlace={handleAddPlaceClick} 
                onEditAvatar={handleEditAvatarClick} 
                onCardClick={handleCardClick} 
                cards={cards} 
                onCardLike={handleLike}
                onCardDelete={handleCardDelete}/>
          <Footer />
        </div>
        <EditProfilePopup isOpen={isEditProfilePopupOpen} 
                          onClose={handleClosePopup} 
                          onUpdateUser={handleUpdateUser}  
                          />
        <PopupWithForm type="delete-confirm" name="avatar-edit-confirm" title="Вы уверены?">
        </PopupWithForm>
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} 
                         onClose={handleClosePopup}
                         onUpdateAvatar={handleUpdateAvatar} />
        <PopupWithForm type="place" name="place-add" title="Новое место" isOpen={isAddPlacePopupOpen} onClose={handleClosePopup}>
            <input id="place-name" type="text" name="place-name" className="popup__input popup__input_content_place-name" placeholder="Название" required minLength="2" maxLength="30"/>
            <span id="error-place-name" className="error-message error-message_visible"></span>
            <input id="image" type="url" name="image" className="popup__input popup__input_content_img" placeholder="Ссылка на картинку" required/>
            <span id="error-image" className="error-message error-message_visible"></span>
        </PopupWithForm>
        {selectedCard && <ImagePopup card={selectedCard} onClose={handleClosePopup}/>}
      </div>
    </CurrentUserContext.Provider>

  );
}

export default App;
