import React, {useEffect} from "react";
import ava from '../images/profile/ava.jpg';
import editButton from '../images/profile/edit-btn-icon.svg';
import {api} from "../utils/api"
import Card from "./Card";

function Main(props) {
  const [userName, setUserName] = React.useState('Жак');
  const [userDescription, setUserDescription] = React.useState('Исследователь');
  const [userAvatar, setUserAvatar] = React.useState(ava);
  const [cards, setCards] = React.useState([]);

  useEffect(() => {
    Promise.all([api.getProfile(), api.getInitialCards()])
      .then(([userInfo, cards]) => {
        console.log(userInfo);
        setUserName(userInfo.name);
        setUserDescription(userInfo.about);
        setUserAvatar(userInfo.avatar);

        setCards(cards);
      })
      .catch(err => {
        console.log('Ошибка: ', err)
      })
    },[],
  );

  return (
      <main>
        <section className="profile">
          <div className="profile__content">
            <div className="profile__avatar-container">
              <img src={userAvatar} alt="Аватар" className="profile__avatar"/>
            <div className="profile__avatar-overlay">
              <img onClick={props.onEditAvatar} src={editButton} alt="Редактировать" className="profile__avatar-edit"/>
            </div>
          </div>
          <div className="profile__info">
            <div className="profile__info-main">
              <h1 className="profile__info-name">{userName}</h1>
              <button onClick={props.onEditProfile} aria-label="Редактироавние профиля" type="button" className="profile__info-edit">
              </button>
            </div>
            <p className="profile__info-job">{userDescription}</p>
          </div>
        </div>
        <button onClick={props.onAddPlace} aria-label="Добавить" type="button" className="profile__add-btn">
        </button>
      </section>
      <section className="elements">
        {cards.map((card) => (
        <Card data={card} key={card._id} onCardClick={props.onCardClick}/>
        ))}
      </section>
      </main>
  );
}

export default Main;
